function svgHoverOn(e){
  let cssfill = e.target.style.fill;
  let idRadialGradient = cssfill.toString().split('"')[1].split('#')[1];
  let radialGradient = document.getElementById(idRadialGradient);
  let rgXlink = radialGradient.getAttribute('xlink:href');
